void reset_mp_options_for_scenario(MPSCENARIOS scenarioid)
{
    scenario = scenarioid;

    unlock_stage_select = 1;
    unlock_game_length = 1;
    unlock_chars = 1;
    unlock_weapon_select = 1;
    unlock_handicap = 1;
    unlock_control_style = 1;
    unlock_aim_sight = 1;

    
    switch(scenario)
    {
    case SCENARIO_NORMAL:
    case SCENARIO_2v2:
    case SCENARIO_3v1:
    case SCENARIO_2v1:
        if (6 < game_length)
        {
            game_length = 2;
        }
        if (0xd < getMPWeaponSet())
        {
            setMPWeaponSet(0xd);
        }
        break;
    case SCENARIO_YOLT:
        game_length = 7;

        if (0xd < getMPWeaponSet())
        {
            setMPWeaponSet(0xd);
        }
        unlock_game_length = 0;
        break;
    case SCENARIO_TLD:
        if (3 < game_length)
        {
            game_length = 2;
        }
        if (0xd < getMPWeaponSet())
        {
            setMPWeaponSet(0xd);
        }
        break;
    case SCENARIO_MWTGG:
        if (6 < game_length)
        {
            game_length = 2;
        }
        setMPWeaponSet(0xd);
        unlock_weapon_select = 0;
        break;
    case SCENARIO_LTK:
        if (6 < game_length)
        {
            game_length = 2;
        }
        setMPWeaponSet(1);
        unlock_handicap = 0;
    }
    return;
}






